// SignUp.js
import React from 'react';
import { Link, useHistory } from 'react-router-dom'; // Import useHistory for redirection
import logoImage from '../assets/Logo.png'; // Import your logo image here
import './SignUp.css';

function SignUp() {
  const history = useHistory(); // Create useHistory instance for redirection

  const handleSubmit = async (event) => {
    event.preventDefault();
    const data = {
        username: event.target.elements.username.value,
        password: event.target.elements.password.value,
        email: event.target.elements.email.value,
        first_name: event.target.elements.firstName.value,
        last_name: event.target.elements.lastName.value,
        mbti: event.target.elements.mbti.value,
        gender: event.target.elements.gender.value,
        mbti_variant: event.target.elements.mbti_variant.value // If you have this field
    };

    const response = await fetch('/api/register/', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
    });

    if (response.ok) {
        history.push('/create-virtual-friend'); // Redirect to create virtual friend page, replace with your route
    } else {
        // Handle error
        alert("Failed to sign up. Please try again.");
    }
  };

  return (
    <div className="signup-container">
      <form className="signup-form" onSubmit={handleSubmit}>
        <img src={logoImage} alt="Emo AI Logo" className="signup-logo" />
        <h1>Sign Up</h1>
        <input type="text" placeholder="Username" name="username" />
        <input type="email" placeholder="E-mail" name="email" />
        <input type="password" placeholder="Password" name="password" />
        <input type="password" placeholder="Confirm Password" name="confirmPassword" />
        <input type="text" placeholder="Last Name" name="lastName" />
        <input type="text" placeholder="First Name" name="firstName" />
        <select name="gender" className="signup-dropdown">
          {/* Gender options */}
        </select>
        <select name="mbti" className="signup-dropdown">
          {/* MBTI options */}
        </select>
        <select name="mbti_variant" className="signup-dropdown">
          {/* MBTI variant options */}
        </select>
        <button type="submit">Sign Up</button>
        <div className="signup-links">
          <Link to="/signin">Already have an account? Sign In</Link>
        </div>
      </form>
    </div>
  );
}

export default SignUp;
